package JavaBasic;

public class Ud4_1APP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaracion de dos variables numéricas
		int X=20, Y=5;
		
		//Impresion en consola de la suma, resta, multiplicacion, division y modulo entre las variables
		System.out.println("La suma de "+X+" + "+Y+" = "+(X+Y));
		System.out.println("La resta de "+X+" - "+Y+" = "+(X-Y));
		System.out.println("La multiplicacion de "+X+" * "+Y+" = "+(X*Y));
		System.out.println("La division de "+X+" / "+Y+" = "+(X/Y));
		System.out.println("El modulo de "+X+" % "+Y+" = "+(X%Y));
	}

}
