package JavaBasic;

public class Ud4_2APP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Declaracion de variables
		int N=3;
		double A=4;
		char C='U';
		
		//Impresion por consola de operaciones con las variables
		System.out.println("El valor de "+"N"+" es "+N+" \n"+"El valor de "+"A"+" es "+A+" \n"+"El valor de "+"C"+" es "+C+" \n ");
		System.out.println("La suma de "+N+" + "+A+"="+(N+A));
		System.out.println("La diferencia de "+A+" - "+N+"="+(A-N));
		System.out.println("El valor numerico de C es "+(int)C);
	}

}
