package JavaBasic;

public class Ud4_3APP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
     	//Declaracion de variables
		int X=4, Y=6;
		double N=2, M=9;
		
		
		//impresion por consola de operaciones entre las variables
		System.out.println("El valor de "+"X"+" es "+X+" \n"+"El valor de "+"Y"+" es "+Y);
		System.out.println("El valor de "+"N"+" es "+N+" \n"+"El valor de "+"M"+" es "+M);
		System.out.println("La suma de "+X+" + "+Y+"="+(X+Y));
		System.out.println("La diferencia de "+X+" - "+Y+"="+(X-Y));
		System.out.println("El producto de "+X+" * "+Y+"="+(X*Y));
		System.out.println("El conciente de "+X+" / "+Y+"="+(X/Y));
		System.out.println("El resto de "+X+" % "+Y+"="+(X%Y));
		
		System.out.println("La suma de "+N+" + "+M+"="+(N+M));
		System.out.println("La diferencia de "+N+" - "+M+"="+(N-M));
		System.out.println("El producto de "+N+" * "+M+"="+(N*M));
		System.out.println("El conciente de "+N+" / "+M+"="+(N/M));
		System.out.println("El resto de "+N+" % "+M+"="+(N%M));
		
		System.out.println("La suma de "+X+" + "+N+"="+(X+N));
		System.out.println("El conciente de "+Y+" / "+M+"="+(Y/M));
		System.out.println("El resto de "+Y+" % "+M+"="+(Y%M));
		
		System.out.println("El doble de "+"X:"+X+" es "+X*2+" \n"+"El doble de "+"Y:"+Y+" es "+Y*2);
		System.out.println("El doble de "+"N:"+N+" es "+N*2+" \n"+"El doble de "+"M:"+M+" es "+M*2);
		
		System.out.println("La suma de "+X+" + "+Y+" + "+N+" + "+M+" = "+(X+Y+N+M));
		System.out.println("El producto de "+X+" * "+Y+" * "+N+" * "+M+" = "+(X*Y*N*M));
	}

}
