package JavaBasic;

public class Ud4_4APP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N=4;

		System.out.println("El valor de N es "+N);
		//Incrementar N en 77.
		N=N+77;
		System.out.println("N "+"+ 77"+" = "+(N));
		//Decrementarla en 3.}
		N=N-3;
		System.out.println("N "+"- 3"+" = "+(N));
		//Duplicar su valor
		N=N*2;
		System.out.println("N "+"* 2"+" = "+(N));
		
	}

}
